import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider } from "@/context/AuthContext";
import ProtectedRoute from "@/components/ProtectedRoute";

// Pages
import Homepage from "@/pages/Homepage";
import Login from "@/pages/Login";
import Contact from "@/pages/Contact";
import PatientDashboard from "@/pages/PatientDashboard";
import DoctorDashboard from "@/pages/DoctorDashboard";
import AdminDashboard from "@/pages/AdminDashboard";
import DoctorList from "@/pages/DoctorList";
import AppointmentBooking from "@/pages/AppointmentBooking";
import Chat from "@/pages/Chat";
import VideoCall from "@/pages/VideoCall";
import Profile from "@/pages/Profile";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      {/* Public routes */}
      <Route path="/" component={Homepage} />
      <Route path="/login" component={Login} />

      {/* Protected routes */}
      <Route path="/patient">
        <ProtectedRoute allowedRoles={['patient']}>
          <PatientDashboard />
        </ProtectedRoute>
      </Route>

      <Route path="/doctor">
        <ProtectedRoute allowedRoles={['doctor']}>
          <DoctorDashboard />
        </ProtectedRoute>
      </Route>

      <Route path="/admin">
        <ProtectedRoute allowedRoles={['admin']}>
          <AdminDashboard />
        </ProtectedRoute>
      </Route>

      {/* Public routes for homepage navigation */}
      <Route path="/doctors" component={DoctorList} />
      <Route path="/appointment-booking" component={AppointmentBooking} />
      <Route path="/contact" component={Contact} />
      <Route path="/about" component={Contact} />

      {/* Protected doctor list for authenticated users */}
      <Route path="/doctors-protected">
        <ProtectedRoute allowedRoles={['patient']}>
          <DoctorList />
        </ProtectedRoute>
      </Route>

      <Route path="/appointment-booking-protected">
        <ProtectedRoute allowedRoles={['patient']}>
          <AppointmentBooking />
        </ProtectedRoute>
      </Route>

      <Route path="/chat">
        <ProtectedRoute>
          <Chat />
        </ProtectedRoute>
      </Route>

      <Route path="/video-call">
        <ProtectedRoute>
          <VideoCall />
        </ProtectedRoute>
      </Route>

      <Route path="/profile">
        <ProtectedRoute>
          <Profile />
        </ProtectedRoute>
      </Route>

      {/* Additional admin routes */}
      <Route path="/manage-doctors">
        <ProtectedRoute allowedRoles={['admin']}>
          <AdminDashboard />
        </ProtectedRoute>
      </Route>

      <Route path="/manage-users">
        <ProtectedRoute allowedRoles={['admin']}>
          <AdminDashboard />
        </ProtectedRoute>
      </Route>

      <Route path="/appointments">
        <ProtectedRoute allowedRoles={['admin']}>
          <AdminDashboard />
        </ProtectedRoute>
      </Route>

      <Route path="/analytics">
        <ProtectedRoute allowedRoles={['admin']}>
          <AdminDashboard />
        </ProtectedRoute>
      </Route>

      <Route path="/patients">
        <ProtectedRoute allowedRoles={['doctor']}>
          <DoctorDashboard />
        </ProtectedRoute>
      </Route>

      {/* Fallback to 404 */}
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;

